import React, { useState } from 'react';
import Dashboard from './components/Dashboard';

function App() {
  // Authentication state and user info (mock)
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);

  // Mock Keycloak login
  const handleLogin = () => {
    // TODO: Replace this with real Keycloak login redirect
    setUser({ name: 'Demo User', email: 'demo@company.com' });
    setIsAuthenticated(true);
    // Example: window.location.href = keycloak.createLoginUrl();
  };

  // Mock Keycloak logout
  const handleLogout = () => {
    // TODO: Replace with Keycloak session termination and token clearing
    setIsAuthenticated(false);
    setUser(null);
    // Example: keycloak.logout();
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <Dashboard
        isAuthenticated={isAuthenticated}
        user={user}
        onLogin={handleLogin}
        onLogout={handleLogout}
      />
    </div>
  );
}

export default App;
